from CvPythonExtensions import *
import random
def isPossible(unit,plot):
	gc = CyGlobalContext()
	valid_terrain_strings = ['LUNAR_DOME','LUNAR_MARE','LUNAR_HIGHLAND','LUNAR_REGOLITH','LUNAR_RILLE','GREEN_MOON']
	valid_terrains = [gc.getInfoTypeForString('TERRAIN_'+t) for t in valid_terrain_strings]
	return plot.getTerrainType() not in valid_terrains
def doOutcome(unit,plot,eDefPlayer, eDefUnitType):
	gc = CyGlobalContext()
	map = CyMap()
	destX = -1
	destY = -1
	numValid = 0
	valid_terrain_strings = ['LUNAR_DOME','LUNAR_MARE','LUNAR_HIGHLAND','LUNAR_REGOLITH','LUNAR_RILLE','GREEN_MOON']
	valid_terrains = [gc.getInfoTypeForString('TERRAIN_'+t) for t in valid_terrain_strings]
	for x in range(map.getGridWidth()):
		for y in range(map.getGridHeight()):
			if map.plot(x,y).getTerrainType() in valid_terrains:
				numValid += 1
				if max(random.random(), 1./numValid) == 1./numValid:
					destX = x
					destY = y
	if destX != -1:
		gc.getPlayer(unit.getOwner()).initUnit(gc.getInfoTypeForString('UNIT_LUNAR_ROVER'),destX,destY,UnitAITypes.NO_UNITAI,DirectionTypes.DIRECTION_SOUTH)
def getDisplay(unit,plot):
	return u"Launch to the Moon"
def getAIValue(unit, plot):
	return 1

from CvPythonExtensions import CyGlobalContext
def isPossible(unit,plot):
	gc = CyGlobalContext()
	if not gc.getPlayer(unit.getOwner()).canFound(plot.getX(),plot.getY()):
		return False
	valid_terrain_strings = ['CISLUNAR_SPACE']
	valid_terrains = [gc.getInfoTypeForString('TERRAIN_'+t) for t in valid_terrain_strings]
	return plot.getTerrainType() in valid_terrains
def doOutcome(unit,plot,eDefPlayer, eDefUnitType):
	gc = CyGlobalContext()
	gc.getPlayer(unit.getOwner()).found(plot.getX(),plot.getY())
